package com.xmy.secondskill.access;import com.xmy.secondskill.entity.User;/** * @author xmy * @date 2021/3/31 9:25 下午 *///存储user对象public class UserContext {    private static ThreadLocal<User> userHolder = new ThreadLocal<User>();    public static void setUser(User user) {        userHolder.set(user);    }    public static User getUser() {        return userHolder.get();    }}