package com.xmy.secondskill.controller;import com.sun.org.apache.xpath.internal.operations.Bool;import com.xmy.secondskill.result.Result;import com.xmy.secondskill.vo.LoginVo;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;/** * @author xmy * @date 2021/3/24 9:53 下午 */@Controller@RequestMapping("/login")public class LoginController {    private static Logger log = LoggerFactory.getLogger(LoginController.class);    @GetMapping("/to_login")    public String toLogin() {        return "login";    }    @PostMapping("/do_login")    @ResponseBody    public Result<Boolean> doLogin(LoginVo loginVo) {        log.info(loginVo.toString());        return Result.success(true);    }}