package com.xmy.secondskill.controller;import com.xmy.secondskill.entity.User;import com.xmy.secondskill.service.UserService;import org.apache.ibatis.annotations.Result;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.ResponseBody;/** * @author xmy * @date 2021/3/22 4:35 下午 */@Controllerpublic class SampleController {    @Autowired    private UserService userService;    @GetMapping("/hello")    public String hello(Model model) {        model.addAttribute("name","许明洋");        return "hello";    }    @ResponseBody    @GetMapping("/db/get")    public String dbGet() {        User user = userService.getById(1);        return user.getName();    }    @ResponseBody    @GetMapping("/db/tx")    public String dbTx() {        boolean flag = userService.tx();        return flag ? "success" : "failed";    }}