package com.xmy.secondskill.controller;import com.xmy.secondskill.entity.User;import com.xmy.secondskill.result.Result;import com.xmy.secondskill.service.UserService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;/** * @author xmy * @date 2021/3/28 5:14 下午 */@Controller@RequestMapping("/user")public class UserController {    @Autowired    UserService userService;    @RequestMapping("/info")    @ResponseBody    public Result<User> info(Model model,User user) {        return Result.success(user);    }}