package com.xmy.secondskill.service;import com.xmy.secondskill.dao.GoodsDao;import com.xmy.secondskill.entity.Goods;import com.xmy.secondskill.entity.MiaoshaGoods;import com.xmy.secondskill.vo.GoodsVo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * @author xmy * @date 2021/3/27 4:23 下午 */@Servicepublic class GoodsService {    @Autowired    private GoodsDao goodsDao;    public List<GoodsVo> listGoodsVo() {        return goodsDao.listGoodsVo();    }    public GoodsVo getGoodsVoByGoodsId(Long goodsId) {        return goodsDao.getGoodsVoByGoodsId(goodsId);    }    public boolean reduceGoods(GoodsVo goods) {        MiaoshaGoods g = new MiaoshaGoods();        g.setGoodsId(goods.getId());        int ret = goodsDao.reduceGoods(g.getGoodsId());        return ret > 0;    }    public List<String> getAllGoods() {        return goodsDao.getAllGoods();    }    public List<Goods> getTestGoods() {        return goodsDao.getTestGoods();    }    public void resetStock(List<GoodsVo> goodsList) {    }}