package com.xmy.secondskill.service;import com.xmy.secondskill.dao.OrderDao;import com.xmy.secondskill.entity.MiaoshaOrder;import com.xmy.secondskill.entity.OrderInfo;import com.xmy.secondskill.entity.User;import com.xmy.secondskill.vo.GoodsVo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import java.util.Date;/** * @author xmy * @date 2021/3/28 1:15 下午 */@Servicepublic class OrderService {    @Autowired    OrderDao orderDao;    public MiaoshaOrder getMiaoShaOrderByUserIdAndGoodsId(Long userId,Long goodsId) {        return orderDao.getMiaoShaOrderByUserIdAndGoodsId(userId,goodsId);    }    public long insert(OrderInfo orderInfo) {        return orderDao.insertOrderInfo(orderInfo);    }    public int insertMiaoshaOrder(MiaoshaOrder miaoshaOrder) {        return orderDao.insertMiaoshaOrder(miaoshaOrder);    }    @Transactional    public OrderInfo createOrder(User user, GoodsVo goods) {        OrderInfo orderInfo = new OrderInfo();        orderInfo.setCreateDate(new Date());        orderInfo.setDeliveryAddrId(0L);        orderInfo.setGoodsCount(1);        orderInfo.setGoodsId(goods.getId());        orderInfo.setGoodsName(goods.getGoodsName());        orderInfo.setGoodsPrice(goods.getMiaoshaPrice());        orderInfo.setOrderChannel(1);        orderInfo.setStatus(0);        orderInfo.setUserId(user.getId());        long orderId = orderDao.insertOrderInfo(orderInfo);        MiaoshaOrder miaoshaOrder = new MiaoshaOrder();        miaoshaOrder.setGoodsId(goods.getId());        miaoshaOrder.setOrderId(orderId);        miaoshaOrder.setUserId(user.getId());        orderDao.insertMiaoshaOrder(miaoshaOrder);        return orderInfo;    }}