package com.xmy.secondskill.util;import org.apache.commons.lang3.StringUtils;import java.util.regex.Matcher;import java.util.regex.Pattern;/** * @author xmy * @date 2021/3/25 9:47 上午 */public class ValidatorUtil {    private static final Pattern mobile_pattern = Pattern.compile("1\\d{10}");    public static boolean isMobile(String str) {        if (StringUtils.isEmpty(str)) {            return false;        }        Matcher m = mobile_pattern.matcher(str);        return m.matches();    }    public static void main(String[] args) {        System.out.println(isMobile("18186144181"));        System.out.println(isMobile("1599343014"));    }}